import React from 'react';
import styles from './Pageheader.module.scss';
import Button from '../button/Button';
import MiraHo from '../../../assets/images/MiraHo.jpg';

const PageHeader: React.FC = () => {

    const sparkleSvg = (
        <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round" className="lucide lucide-sparkles w-5 h-5" aria-hidden="true" data-component-path="src/pages/Portfolio.tsx" data-component-line="230" data-component-file="Portfolio.tsx" data-dv-id="src/pages/Portfolio.tsx:230:16" data-dv-name="Sparkles"><path d="M9.937 15.5A2 2 0 0 0 8.5 14.063l-6.135-1.582a.5.5 0 0 1 0-.962L8.5 9.936A2 2 0 0 0 9.937 8.5l1.582-6.135a.5.5 0 0 1 .963 0L14.063 8.5A2 2 0 0 0 15.5 9.937l6.135 1.581a.5.5 0 0 1 0 .964L15.5 14.063a2 2 0 0 0-1.437 1.437l-1.582 6.135a.5.5 0 0 1-.963 0z"></path><path d="M20 3v4"></path><path d="M22 5h-4"></path><path d="M4 17v2"></path><path d="M5 18H3"></path></svg>
    );

    const downloadIcon = (
        <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round" className="lucide lucide-download w-5 h-5" aria-hidden="true" data-component-path="src/pages/Portfolio.tsx" data-component-line="235" data-component-file="Portfolio.tsx" data-dv-id="src/pages/Portfolio.tsx:235:16" data-dv-name="Download"><path d="M12 15V3"></path><path d="M21 15v4a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2v-4"></path><path d="m7 10 5 5 5-5"></path></svg>
    );

    return (
        <>
            <header className={styles.pageheader}>
                <div className={styles.pageheader__content}>
                    <div className={styles.pageheader__profilepic}>
                        <img src={MiraHo} alt="Mira Ho" />
                    </div>
                    <h1>Hi, ich bin Mira ðŸ‘‹</h1>
                    <h2>Creative Frontend Developer</h2>
                    <p className={styles.pageheader__description}>I'm a passionate frontend developer who believes in the power of storytelling through code. I transform ideas into beautiful, interactive digital experiences that not only look stunning but also create meaningful connections with users.</p>
                    <div className={styles.pageheader__buttons}>
                        <Button onClick={() => alert('Button clicked!')}>{sparkleSvg} Portfolio</Button>
                        <Button onClick={() => alert('Button clicked!')}>{downloadIcon} View Portfolio</Button>
                    </div>
                </div>
            </header>
        </>
    );
};

export default PageHeader;
