import React from 'react';
import styles from './Section.module.scss';

interface SectionProps {
    children: React.ReactNode;
    backgroundColor?: 'pink' | 'orange' | 'purple';
}

const Section: React.FC<SectionProps> = ({ children, backgroundColor }) => {

    const bgColor = () => {
        switch (backgroundColor) {
            case 'pink':
                return 'linear-gradient(135deg, #ffe7f5 0%, #ffceea 100%)';
            case 'orange':
                return 'linear-gradient(135deg, #fff1e7 0%, #ffe2cd 100%)';
            case 'purple':
                return 'linear-gradient(135deg, #f2e8ff 0%, #e4cfff 100%)';
            default:
                return 'transparent';
        }
    }

    return <div className={styles['section-container']} style={{ backgroundColor: bgColor() }}>{children}</div>;
};

export default Section;
