import React, { createContext, useContext, useState } from "react";

interface ModalContextType {
  isOpen: (id: string) => boolean;
  openModal: () => void;
  closeModal: () => void;
}

const ModalContext = createContext<ModalContextType | undefined>(undefined);

export const ModalProvider: React.FC<{ children: React.ReactNode }> = ({ children }) => {
  // Initialize openSheets with all possible bottom sheet IDs set to false
  const [openSheets, setOpenSheets] = useState<Record<string, boolean>>({});

  const isOpen = (id: string) => !!openSheets[id]; // Check if a specific bottom sheet is open

  const openModal = () => {
    console.log("open Modal", id);
    setOpenSheets((prev) => ({ ...prev, [id]: true })); // Set the specific bottom sheet to open
  };

  const closeModal = () => {
    console.log("close Modal", id);
    setOpenSheets((prev) => ({ ...prev, [id]: false })); // Set the specific bottom sheet to closed
  };

  return (
    <ModalContext.Provider value={{ isOpen, openModal, closeModal }}>
      {children}
    </ModalContext.Provider>
  );
};

export const useModal = () => {
  const context = useContext(ModalContext);
  if (!context) {
    throw new Error("useModal must be used within a ModalProvider");
  }
  return context;
};
