import React from "react";
import styles from "./Teaser.module.scss";

interface TeaserProps {
    title: string;
    text: string;
    imgPath?: string;
}

const Teaser: React.FC<TeaserProps> = ({ title, text = "", imgPath }) => {
    return (
        <div className={styles.teaser}>
            {imgPath && <img src={imgPath} alt={title} className={styles.image} />}
            <h3>{title}</h3>
            <p>{text}</p>
        </div>
    );
};

export default Teaser;
