import React from "react";
import styles from "./Teaser.module.scss";
import Button from "../button/Button";

interface TeaserProps {
    title: string;
    text: string;
    imgPath?: string;
}

const Teaser: React.FC<TeaserProps> = ({ title, text = "", imgPath }) => {

    const openModal = () => {
        console.log("Teaser clicked:", title);
    }

    return (
        <div className={styles.teaser}>
            <img src={imgPath ? imgPath : "https://placehold.co/600x400"} alt={title} className={styles.image} />
            <h3>{title}</h3>
            <p>{text}</p>
            <Button type="tertiary" onClick={openModal}>Read more</Button>
        </div>
    );
};

export default Teaser;
