import React from "react";
import styles from "./Storybox.module.scss";

type Color = "orange" | "pink" | "purple";

interface StoryboxProps {
    icon: string; // an emoji or short text used as an icon
    color?: Color;
    text: string;
    className?: string;
}

const colorMap: Record<Color, { bg: string; fg: string }> = {
    orange: { bg: "#FFF4E6", fg: "#D35400" },
    pink: { bg: "#FFF0F6", fg: "#C2185B" },
    purple: { bg: "#F5F3FF", fg: "#6D28D9" },
};

const Storybox: React.FC<StoryboxProps> = ({ icon, color = "purple", text, className = "" }) => {
    const { bg, fg } = colorMap[color];

    return (
        <div
            className={styles.storybox + " " +  className}
            style={{
                display: "flex",
                alignItems: "center",
                gap: 12,
                userSelect: "none",
                border: "1px solid" + bg,
            }}
            aria-label={text}
        >
            <div
                role="img"
                aria-hidden
                style={{
                    display: "inline-flex",
                    alignItems: "center",
                    justifyContent: "center",
                    width: 40,
                    height: 40,
                    minWidth: 40,
                    borderRadius: 8,
                    background: bg,
                    color: fg,
                    fontSize: 20,
                    lineHeight: 1,
                }}
            >
                {icon}
            </div>

            <div style={{ fontSize: 14, color: "#0F172A" }}>{text}</div>
        </div>
    );
};

export default Storybox;
