import React from 'react';
import styles from './Section.module.scss';

interface SectionProps {
    children: React.ReactNode;
    backgroundColor?: 'pink' | 'orange' | 'purple' | 'none';
}

const Section: React.FC<SectionProps> = ({ children, backgroundColor }) => {

    const bgClass = backgroundColor ? styles[backgroundColor] : '';

    return <div className={`${styles['section-container']} ${bgClass}`}>{children}</div>;
};

export default Section;
