import React, { useState } from "react";
import styles from "./Modal.module.scss";

const Modal: React.FC = () => {

    const [isOpen, setIsOpen] = useState(false);

    React.useEffect(() => {
        if (isOpen) {
            document.body.style.overflow = 'hidden';
        } else {
            document.body.style.overflow = 'unset';
        }
        return () => {
            document.body.style.overflow = 'unset';
        };
    }, [isOpen]);

    return (
        <div className={`${styles.modal} ${isOpen ? styles['modal--open'] : ''}`}>
            <div className={styles.content}>
            <div className={styles.close} onClick={() => setIsOpen(false)}>X</div>
            <h2 className={styles.title}>Modal Title</h2>
            <p className={styles.content}>This is the modal content.</p>
            </div>
        </div>
    );
};

export default Modal;
