import React, { useEffect, useState } from 'react';
import styles from './Section.module.scss';

interface SectionProps {
    children: React.ReactNode;
    backgroundColor?: 'pink' | 'orange' | 'purple' | 'none';
    className?: string;
}

const Section: React.FC<SectionProps> = ({ children, backgroundColor, className }) => {

    const bg = backgroundColor ?? 'none';
    const additionalClasses = className ? className : '';

    return <div className={`${styles['section-container']} ${styles[bg]} ${additionalClasses}`}>{children}</div>;
};

export default Section;
